'use strict';

(function ($, Drupal) {
  Drupal.behaviors.MyThemeAddMyThingDialog = {
    attach: function attach(context, settings) {
      $(context).find('a.use-ajax.my-extra-class').once('my-extra-class-processed').each(function (element_index, element) {
        $.each(Drupal.ajax.instances, function (index, instance) {
          if (!instance.element || !$(element).is(instance.element)) {
            return true;
          }

          var classSuffixes = ['--mything'];
          var dialogOptions = {
            'classes': {
              'ui-dialog': $.map(classSuffixes, function (e) {
                return 'ui-dialog'.concat(e);
              }).join(' '),
              'ui-dialog-off-canvas': $.map(classSuffixes, function (e) {
                return 'ui-dialog-off-canvas'.concat(e);
              }).join(' '),
              "ui-dialog-titlebar": $.map(classSuffixes, function (e) {
                return 'ui-dialog-titlebar'.concat(e);
              }).join(' '),
              "ui-dialog-title": $.map(classSuffixes, function (e) {
                return 'ui-dialog-title'.concat(e);
              }).join(' '),
              "ui-dialog-titlebar-close": $.map(classSuffixes, function (e) {
                return 'ui-dialog-titlebar-close'.concat(e);
              }).join(' '),
              "ui-dialog-content": $.map(classSuffixes, function (e) {
                return 'ui-dialog-content'.concat(e);
              }).join(' '),
              "ui-dialog-buttonpane": $.map(classSuffixes, function (e) {
                return 'ui-dialog-buttonpane'.concat(e);
              }).join(' ')
            },
            is_my_thing: true,
            width: 300
          };
          instance.elementSettings.dialog = $.extend(true, instance.elementSettings.dialog, dialogOptions);
          instance.options.data.dialogOptions = $.extend(true, instance.options.data.dialogOptions, dialogOptions);
        });
      });
    }
  };
  $(window).on('dialog:beforecreate', function (e, dialog, $element, settings) {
    if (settings.is_my_thing) {
      $('body').addClass('modal-dialog-open--off-canvas--my-thing');
    }
  });
  $(window).on('dialog:beforeclose', function (e, dialog, $element) {
    var settings = $element.dialog('option');

    if (settings.is_my_thing) {
      $('body').removeClass('modal-dialog-open--off-canvas--my-thing');
      $('.another-element-resize').removeAttr('style');
    }
  });
  $(window).on('dialog:aftercreate', function (e, dialog, $element, settings) {
    if (settings.is_my_thing) {
      var eventData = {
        settings: settings,
        $element: $element,
        offCanvasDialog: this
      };
      $element.on('dialogContentResize.off-canvas', eventData, function (event) {
        var settings = event.data.settings;
        var $element = event.data.$element;
        var $container = Drupal.offCanvas.getContainer($element);
        var width = $container.outerWidth();
        $('.another-element-resize').css('height', width);
      });
    }
  });
})(jQuery, Drupal);